import React, { Component } from 'react';
import './App.css';
import Navigation from './Navigation';
import Logo from './Logo';
import Rank from './Rank';
import ImageLinkForm from './ImageLinkForm';
import ImageDisplay from './ImageDisplay';
import Clarifai from 'clarifai';
import Particles from 'react-particles-js';

const app = new Clarifai.App({
 apiKey: '3745d0a301034ace84c061999c668112'
});

class App extends Component {
  constructor()
  {
    super();
    this.state=
    {
      input:'',
      imageUrl:'',
      box: {},
    }
  }

  displayFaceBox = (box) =>{
    console.log(box);
    this.setState({box: box});
  }

  calculateFaceLocation=(info)=>{
    const clarifaiFace = info.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.getElementById('inputimage');
    const width = Number(image.width);
    const height = Number(image.height);
    return{
      leftCol : clarifaiFace.left_col * width,
      topRow : clarifaiFace.top_row * height,
      rightCol : width - (clarifaiFace.right_col * width),
      bottomRow : height - (clarifaiFace.bottom_row * height)
    }
  }

  onInputChange=(event)=>{
  this.setState({input: event.target.value});
  }

  onSubmit=()=>
  {
    this.setState({imageUrl: this.state.input});
    app.models.predict("a403429f2ddf4b49b307e318f00e528b", this.state.input)
    .then(response=> this.displayFaceBox(this.calculateFaceLocation(response)))
    .catch( err => console.log(err));


  }

  render() {
    return (
      <div className="App">
           <Particles className='parti'
           params={{
               particles: {
                 number:{
                   value:40,
                   density:{
                     enable:true,
                     value_area:800
                   }
                 },
                move:
                {
                  enable:true,
                  speed:7
                },

               }
             }}

               />
           <Navigation />
           <Logo />
           <Rank />
           <ImageLinkForm onInputChange={this.onInputChange} onSubmit={this.onSubmit} />
           <ImageDisplay box= {this.state.box} imageUrl={this.state.imageUrl} />

      </div>
    );
  }
}

export default App;
